pub type EncodingResult<T> = Result<T, EncodingError>;


#[derive(Error, Debug)]
pub enum EncodingError {
    #[error(r#"CBOR conversion failed: {0}"#)]
    CborConversionFailed(#[from] serde_cbor::Error),
    #[error(r#"JSON conversion failed: {0}"#)]
    JsonConversionFailed(#[from] serde_json::Error),
    #[error(r#"CANDID conversion failed: {0}"#)]
    CandidConversionFailed(#[from] candid::Error),
    #[error(r#"Cannot calculate a RequestID: {0}"#)]
    CannotCalculateRequestId(#[from] RequestIdError),
    #[error(r#"Found {0} request IDs in read_state paths, expected: 1"#)]
    UnexpectedNumberOfRequestIdsFound(u32),
}

fn get_request_id(content: &EnvelopeContent) -> EncodingResult<Vec<u8>> {
    match content {
        EnvelopeContent::Call { .. } | EnvelopeContent::Query { .. } => Ok(to_request_id(&content)?.to_vec()),
        EnvelopeContent::ReadState { paths, .. } => {
            let mut rid_label = vec![];
            for path in paths {
                if path.len() > 1 && path[0].as_bytes().eq("request_status".as_bytes()) {
                    if rid_label.len() > 1 {
                        return Err(UnexpectedNumberOfRequestIdsFound(2));
                    }
                    rid_label.push(path[1].clone())
                }
            }
            if rid_label.len() == 0 {
                Err(UnexpectedNumberOfRequestIdsFound(0))
            } else {
                Ok(rid_label[0].as_bytes().into())
            }
        }
    }
}


#[derive(Clone)]
enum MessageType {
    REQUEST,
    RESPONSE,
}

#[derive(Clone)]
struct IdlConfig {
    api: String,
    direction: MessageType,
    method: String,
}

impl IdlConfig {
    fn from(api: String, direction: MessageType, method: String) -> IdlConfig {
        IdlConfig {
            api,
            direction,
            method,
        }
    }
}

impl From<CanisterInterfaceInfo> for IdlConfig {
    fn from(value: CanisterInterfaceInfo) -> Self {
        IdlConfig {
            api: value.canister_interface,
            direction: RESPONSE,
            method: value.canister_method,
        }
    }
}

fn vec_to_idl_arg_factory(arg: Vec<u8>, idl: &Option<IdlConfig>) -> EncodingResult<IDLArgs> {
    match idl {
        None => { Ok(IDLArgs::from_bytes(&arg)?) }
        Some(config) => {
            let idl = CandidSource::Text(&*config.api);
            let (type_env, actor) = idl.load()?;
            let actor = actor.ok_or(CandidConversionFailed(candid::Error::msg("no actor found")))?;
            let func = type_env.get_method(&actor, &*config.method)?;
            let types = match config.direction {
                REQUEST => { (&func.args).clone() }
                RESPONSE => { (&func.rets).clone() }
            };
            Ok(IDLArgs::from_bytes_with_types(&arg, &type_env, &types)?)
        }
    }
}

fn sender_info(env: Envelope, sender: Principal) -> RequestSenderInfo {
    let delegation = match env.sender_delegation {
        None => vec![],
        Some(del_list) => {
            let mut res = vec![];
            for del in del_list {
                res.push(RequestSenderDelegation {
                    pubkey: del.delegation.pubkey,
                    expiration: del.delegation.expiration,
                    targets: match del.delegation.targets {
                        None => { vec![] }
                        Some(t) => { t }
                    },
                    signature: del.signature,
                });
            }
            res
        }
    };
    RequestSenderInfo {
        sender,
        pubkey: env.sender_pubkey,
        sig: env.sender_sig,
        delegation,
    }
}

pub fn get_request_metadata(encoded_cbor_request: Vec<u8>) -> EncodingResult<RequestMetadata> {
    let env: Envelope = serde_cbor::from_slice(&encoded_cbor_request)?;
    let request_id = get_request_id(&env.content)?;
    match env.content.clone().into_owned().into() {
        EnvelopeContent::Call { method_name, sender, .. } => {
            Ok(RequestMetadata::Call {
                request_id,
                sender_info: sender_info(env, sender),
                canister_method: method_name,
            })
        }
        EnvelopeContent::ReadState { sender, .. } => {
            Ok(RequestMetadata::ReadState { request_id, sender_info: sender_info(env, sender) })
        }
        EnvelopeContent::Query { method_name, sender, .. } => {
            Ok(RequestMetadata::Query {
                request_id,
                sender_info: sender_info(env, sender),
                canister_method: method_name,
            })
        }
    }
}

pub fn decode_canister_request(encoded_cbor_request: Vec<u8>, canister_interface: Option<String>) -> EncodingResult<RequestInfo> {
    let env: Envelope = serde_cbor::from_slice(&encoded_cbor_request)?;
    let request_id = get_request_id(&env.content)?;
    let pretty: EnvelopePretty = env.clone().into();
    let env_json = serde_json::to_string_pretty(&pretty)?;

    match env.content.clone().into_owned().into() {
        EnvelopeContent::Call { arg, method_name, sender, .. } => {
            let idl_config = match canister_interface {
                None => None,
                Some(api) => {
                    Some(IdlConfig {
                        api,
                        direction: REQUEST,
                        method: method_name.clone(),
                    })
                }
            };
            let candid_pretty = vec_to_idl_arg_factory(arg, &idl_config)?.to_string();
            let decoded_request = env_json.replace("ARG_PLACEHOLDER", &*candid_pretty);
            Ok(RequestInfo::Call {
                request_id,
                sender_info: sender_info(env, sender),
                decoded_request,
                canister_method: method_name,
            })
        }
        EnvelopeContent::Query { arg, method_name, sender, .. } => {
            let idl_config = match canister_interface {
                None => None,
                Some(api) => {
                    Some(IdlConfig {
                        api,
                        direction: REQUEST,
                        method: method_name.clone(),
                    })
                }
            };
            let candid_pretty = vec_to_idl_arg_factory(arg, &idl_config)?.to_string();
            let decoded_request = env_json.replace("ARG_PLACEHOLDER", &*candid_pretty);
            Ok(RequestInfo::Query {
                request_id,
                sender_info: sender_info(env, sender),
                decoded_request,
                canister_method: method_name,
            })
        }
        EnvelopeContent::ReadState { sender, .. } => {
            Ok(RequestInfo::ReadState {
                request_id,
                sender_info: sender_info(env, sender),
                decoded_request: env_json,
            })
        }
    }
}

pub fn decode_canister_response(encoded_cbor_response: Vec<u8>, canister_interface_info: Option<CanisterInterfaceInfo>) -> EncodingResult<String> {
    let response = serde_cbor::from_slice::<QueryResponse>(&encoded_cbor_response);
    let idl_config: Option<IdlConfig> = match canister_interface_info {
        None => None,
        Some(info) => Some(info.into()),
    };
    match response.ok() {
        Some(query_response) => decode_query_response(query_response, idl_config),
        None => {
            let readstate_response = serde_cbor::from_slice::<ReadStateResponse>(&encoded_cbor_response)?;
            decode_readstate_response(readstate_response, idl_config)
        }
    }
}

fn decode_query_response(query_response: QueryResponse, idl_config: Option<IdlConfig>) -> EncodingResult<String> {
    let pretty: QueryResponsePretty = query_response.clone().into();
    let query_json = serde_json::to_string_pretty(&pretty).unwrap();

    match query_response.clone() {
        QueryResponse::Replied { reply: x, .. } => {
            let candid_pretty = vec_to_idl_arg_factory(x.arg, &idl_config)?.to_string();
            Ok(query_json.replace("ARG_PLACEHOLDER", &*candid_pretty))
        }
        QueryResponse::Rejected(_) => {
            Ok(query_json)
        }
    }
}

fn decode_readstate_response(readstate_response: ReadStateResponse, idl_config: Option<IdlConfig>) -> EncodingResult<String> {
    let response_pretty: ReadStateResponsePretty = readstate_response.into();
    let mut response_json = serde_json::to_string(&response_pretty).unwrap();
    let finding = find_candid_in_tree(HashTreeNode::from(response_pretty.certificate.tree));
    for candid in finding {
        let candid_json = serde_json::to_string(&candid).unwrap();
        let candid_pretty = vec_to_idl_arg_factory(candid, &idl_config)?.to_string();
        response_json = response_json.replace(&*candid_json, &*candid_pretty);
    }
    Ok(response_json)
}

fn find_candid_in_tree(root: HashTreeNode<Vec<u8>>) -> Vec<Vec<u8>> {
    match root {
        HashTreeNode::Empty() => { vec![] }
        HashTreeNode::Fork(next) => {
            let foo = *next;
            let mut left = find_candid_in_tree(foo.0);
            let mut right = find_candid_in_tree(foo.1);
            left.append(&mut right);
            left
        }
        HashTreeNode::Labeled(_, next) => {
            find_candid_in_tree(*next)
        }
        HashTreeNode::Leaf(x) => {
            if x[0..4] == [0x44, 0x49, 0x44, 0x4C] {
                vec![x]
            } else {
                vec![]
            }
        }
        HashTreeNode::Pruned(_) => { vec![] }
    }
}

